data KD = KDecl Id [Field] [Arg] [Assignment]
  deriving (Eq, Ord, Show, Read)

data Field = Field Id Id
  deriving (Eq, Ord, Show, Read)

data FormalArg = FormalArg Id Id
  deriving (Eq, Ord, Show, Read)

data Arg = Arg Id
