data Term
    = TermVar Id
    | TermFieldAccess Term Id
    | TermMethodInvoc Term Id [Term]
    | TermObjectCreation Id [Term]
    | TermCast Id Term
  deriving (Eq, Ord, Show, Read)

data Id = Identifier String
  deriving (Eq, Ord, Show, Read)

